var db    = {};
var mysql = require('mysql');
var pool  = mysql.createPool({
    connectionLimit : 100,
    host            : '127.0.0.1',
    user            : 'root',
    password        : '_root_',
    database        : 'ssz'
});

db.query = function(sql,sqlParam, callback){
    if (!sql) {
        callback();
        return;
    }
    pool.query(sql,sqlParam, function(err, rows, fields) {
        if (err) {
            console.log(err);
            callback(err, null);
            return;
        };

        callback(null, rows, fields);
    });
};

module.exports = db;
